import React from 'react'

function Avatar () {
  return (
    <figure>
      <img src="img/avatar.jpg"></img>
      <style jsx>{`
        figure {
          margin: 0;
          padding: 0;
          display: contents;
        }

        img {
          width: 50%;
          max-width: 200px;
          border-radius: 50%;
          border: 5px solid white;
        }

        @media screen and (min-width: 768px) {
          img {
            width: 200px;
          }
        }
      `}</style>
    </figure>
  )
}

export default Avatar
