import React from 'react'
import Avatar from '../components/Avatar'

function Home() {
  return (
    <>
      <header>
        <h1 className="title">FA.</h1>
        <div className="contact-info">
          <p>(+57 311 349 5655)</p>
          <p>facosta0787@gmail.com</p>
        </div>
      </header>
      <main>
        <div className="avatar-container">
          <Avatar />
        </div>
        <h2>FELIPE ACOSTA</h2>
        <h4>Front-End Developer</h4>
      </main>

      <style jsx>{`
        header {
          display: flex;
          justify-content: space-between;
          padding-top: 40px;
        }

        header > .title {
          margin: 0;
          padding: 0;
          font-size: 30px;
          margin-left: 40px;
          text-shadow: 0 3px 8px rgba(0, 0, 0, 0.10);
        }

        header > .contact-info {
          margin-right: 20px;
          font-size: 14px;
          font-family: 'Lora';
          font-style: italic;
          color: #fafafa;
        }

        header > .contact-info > p:first-child {
          opacity: 0.5;
          margin-bottom: 3px;
        }

        main {
          display: flex;
          justify-content: center;
          flex-direction: column;
          text-align: center;
        }

        .avatar-container {
          margin-top: 30%;
          display: flex;
          justify-content: center;
        }

        main > h2 {
          margin-top: 40px;
          font-size: 30px;
        }

        main > h4 {
          font-family: 'Lora';
          font-style: italic;
          font-size: 14px;
        }
      `}</style>

      <style jsx global>{`
        * {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }

        body {
          height: 100vh;
          font-family: 'Montserrat', sans-serif;
          color: #fff;
          background-color: #323232;
        }

        #__next {
          background-image: linear-gradient(147deg, #323232 0%, #212121 74%);
          height: 100%;
        }
      `}</style>
    </>
  )
}

export default Home
