import React from 'react'
import Avatar from '../components/Avatar'

function Home() {
  return (
    <>
      <header>
        <h1 className="title">FA.</h1>
        <div className="contact-info">
          <p>(+57 311 349 5655)</p>
          <p>facosta0787@gmail.com</p>
        </div>
      </header>
      <main>
      </main>
      <style>{`
        header {
          display: flex;
          justify-content: space-between;
          padding-top: 40px;
        }

        header > .title {
          margin-left: 40px;
        }

        header > .contact-info {
          margin-right: 20px;
          font-size: 14px;
          font-family: 'Lora';
          font-style: italic;
          color: #fafafa;
        }

        header > .contact-info > p:first-child {
          opacity: 0.5;
        }

        main {
          width: 100%;
          height: 100vh;
        }

        .title {
          margin: 0;
          padding: 0;
          font-size: 30px;
        }
      `}</style>

      <style jsx global>{`
        * {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }

        body {
          height: 100vh;
          font-family: 'Montserrat', sans-serif;
          color: #fff;
          background-color: #323232;
        }

        #__next {
          background-image: linear-gradient(147deg, #323232 0%, #212121 74%);
          height: 100%;
        }
      `}</style>
    </>
  )
}

export default Home
